import React from 'react'
import { DataGrid } from '@material-ui/data-grid'

const cellRenderer = data => {
  console.log(data.row.kafka_offset)
  return data.row.kafka_offset
  // return <a href={data.row.kafka_offset} target='_blank' rel='noopener noreferrer'>{data.row.kafka_offset}</a>
}

const columns = [
  {
    cellRenderer: ({row}) => <a href='aap'>{row.name}</a>
  },
  { field: 'kafka_topic', headerName: 'Kafka Topic', flex: 1 },
  { field: 'kafka_offset', headerName: 'Kafka Offset', flex: 0.4, valueGetter: cellRenderer },
  { field: 'identifier_type', headerName: 'Identifier Type', flex: 0.6 },
  { field: 'identifier_value', headerName: 'Identifier Value', flex: 1 },
]

const pad = (num, size) => {
  num = num.toString()
  while (num.length < size) num = '0' + num
  return num
}

const Lister = ({data, limit}) => {
  return (
    <div style={{ height: '82vh', width: '100%' }}>
      {(data.length > 0 && data.length < limit) && <DataGrid hideFooter rows={data.map(d => {
        d.kafka_offset = pad(parseInt(d.kafka_offset), 10)
        return { ...d, id: Math.random().toString(20)}
      })} columns={columns} />}
      {data.length >= limit && <p style={{fontSize: 14}}>Too many rows</p>}
    </div>
  )
}

export default Lister
