import { useState, useEffect } from 'react'
import './App.css'
import { TextField } from '@material-ui/core'
import axios from 'axios'
import Lister from './components/Lister'

function App() {

  const [query, setQuery] = useState('')
  const [data, setData] = useState([])

  const onChange = async evt => setQuery(evt.target.value)

  useEffect(async _ => {
    if (query) {
      try {
        await axios.post('http://localhost:3334/api/v1/search/', {
          search: query
        }, {
          mode: 'no-cors'
        })
        .then(res => {
          setData(res.data)
        })        
      } catch(err) {
        console.warn(err.message)
        setData([])
      }
    } else {
      setData([])
    }
  }, [query])

  return (
    <div className="App">
      <header style={{ border: '1px solid red' }} className="App-header">
        <div>
          <TextField
            margin='dense'
            variant='filled'
            onChange={onChange}
            value={query}
            color='primary'
            type='search'
            label='search'
            placeholder='...'
          /> {/* text color in App.css: input */}
        </div>
        <Lister data={data} />
      </header>
    </div>
  )

}

export default App
