import React from 'react'
import { useEffect, useState } from 'react'
import axios from 'axios'
import { ScaleLoader } from 'react-spinners'
// import { cancelTokenSource } from 'axios'

const Details = props => {

  const [ message, setMessage ] = useState({})
  const [ loading, setLoading ] = useState(false)
  const { topic, offset } = props.match.params
  const { identifierType } = props.location.state || {}
  const { identifierValue } = props.location.state || {}

  useEffect(async _ => {
    try {
      setLoading(true)
      await axios.post('https://offset.fhirstation.net/api/v1/kafka/', {
        // cancelToken: cancelTokenSource.token,
        topic, offset
      }, {
        mode: 'no-cors'
      })
      .then(res => {
        setMessage(res.data)
        setLoading(false)
      })
    } catch(err) {
      console.warn(err.message)
      setLoading(false)
    }
  }, [])

  console.log('Message:', message)

  return (
    <div style={{display: 'flex', justifyContent: 'center', height: '94vh'}}>
      <div style={{display: 'flex', alignItems: 'center'}}>
        <div>
          <h2>{topic}/{offset}</h2>
          {loading ? <div>please wait, loading from kafka...</div> : <div>{JSON.stringify(message)}</div>}
        </div>
      </div>
    </div>
  )
}

export default Details
