import { useState, useEffect } from 'react'
import './App.css'
import { TextField } from '@material-ui/core'

function App() {

  const [query, setQuery] = useState('')
  const [data, setData] = useState([])
  const [loading, setLoading] = useState(false)

  const onChange = async evt => setQuery(evt.target.value)

  useEffect(async _ => {
    const response = await fetch('http://localhost:3334/api/v1/search', {
      method: 'POST',
      mode: 'no-cors',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ search: query })
    })
      .then(res => {
        console.log('query', query)
        // res.json()
        return ''
      })
    console.log('RESPONSE', response)
    setData(response)
  }, [query])

  return (
    <div className="App">
      <header className="App-header">
      <TextField
        variant='outlined'
        onChange={onChange}
        value={query}
        color='primary'
        type='search'
        label='search'
        placeholder='...'
      /> {/* text color in App.css: input */}
      <ul>
        {Array.isArray(data) ? data.map(d => <li key={d.title}>{d.title}</li>) : data.title ? <li>{data.title}</li> : <li>nothing here...</li>}
        </ul>
      </header>
    </div>
  )

}

export default App
